sql.where:
	    type = 1	
		 and status = #{loanStatus}
    	and real_name = #{realName}
    	and mobile = #{mobile}
    	 and release_date > #{arrivedDate1}
    	and release_date <= #{arrivedDate2}
    	and create_date > #{applyDate1}
    	and create_date <= #{applyDate2}
    	and user_Id = #{userId}

mapper.select:
select 
from t_esp_award_detail
where user_id = #{userId} and status in(0,2) and create_date between #{beginMonthDay} and #{now}

select 
from t_esp_award_detail
where  status =#{status} and type = #{type}

        select SUM(amount)
	    from t_esp_award_detail	

		   	and create_date > #{applyDate1}
	    	and create_date <= #{applyDate2}
	    	and user_Id = #{userId}
	    	and type = #{type}

        select count(1) 
	    from t_esp_award_detail	

	    	and user_Id = #{userId}
	    	and type = #{type}
	    	and order_id = #{orderId}
	    	and activity_id = #{activityId}

