mapper.select:
SELECT id id, limit_buy_act_id limitBuyActId, goods_id goodsId, sku_id skuId, market_price marketPrice, activity_price activityPrice, limit_num_total limitNumTotal, limit_num limitNum, limit_curr_total limitCurrTotal, sort_no sortNo, url url, sku_id skuId, create_user createUser, update_user updateUser, created_time createdTime, updated_time updatedTime FROM t_esp_limit_goods_sku WHERE 1=1
AND limit_buy_act_id = #{limitBuyActId} AND goods_id = #{goodsId} AND sku_id = #{skuId}
ORDER BY sort_no ASC
