select tx.txn_id txnId, tx.order_id mainOrderId, ord.order_id orderId, ord.orderstatus orderStatus, ord.pay_time payTime, ord.id extOrderId, tx.user_id userId, tx.txn_type txnType, tx.txn_date txnDate, tx.txn_amt txnAmt, tx.post_date postDate, tx.txn_desc txnDesc, tx.orig_txn_id origTxnId, tx.orig_trans_date origTransDate, tx.loan_id loanId, tx.status status, tx.CREATE_DATE createDate from t_apass_txn_info tx left join t_esp_order_info ord on tx.order_id = ord.main_order_id where tx.txn_type = 'T04' AND tx.status = 'S' AND tx.settle_flag != 0 AND ord.ext_parent_id!='-1' AND ord.orderstatus in
#{item}
and ord.update_date >= #{dateBegin} and ord.update_date < #{dateEnd}

