mapper.update:

	UPDATE t_esp_address_info SET is_default = "0"
	WHERE is_default = "1"
	AND user_id = #{userId}

mapper.delete:
DELETE FROM t_esp_address_info where id in

	#{idItem}

AND USER_ID = #{userId}

mapper.select:
SELECT id id, id addressId, user_id userId, province province, city city, district district, towns towns, province_code provinceCode, city_code cityCode, district_code districtCode, towns_code townsCode, address address, postcode postcode, name name, telephone telephone, is_default isDefault FROM t_esp_address_info
1=1
AND id = #{id, jdbcType=BIGINT}
AND is_default = #{isDefault, jdbcType=VARCHAR}
AND user_id = #{userId, jdbcType=BIGINT}
ORDER BY id DESC
