mapper.select:
SELECT a.ID goodId, c.goodsStockId goodsStockId, a.goods_name goodsName, a.goods_title goodsTitle, a.goods_sell_pt goodsSellPt, a.goods_logo_url goodsLogoUrl, c.minPrice marketPrice FROM t_esp_goods_base_info a, ( SELECT a.goods_id goodsId, a.id goodsStockId, b.minprice minPrice FROM t_esp_goods_stock_info a, ( SELECT goods_id goodsId, MIN(market_price) minprice FROM t_esp_goods_stock_info WHERE stock_curr_amt > 0 GROUP BY goods_id ) b WHERE a.goods_id = b.goodsId AND a.market_price = b.minprice GROUP BY a.goods_id ) c WHERE a.id = c.goodsId AND CURRENT_TIMESTAMP > a.list_time AND CURRENT_TIMESTAMP < a.delist_time AND STATUS = 'G02' AND is_delete = '01'
AND a.category_id1 = #{categoryId1, jdbcType=BIGINT}
AND a.category_id2 = #{categoryId2, jdbcType=BIGINT}
AND a.category_id3 = #{categoryId3, jdbcType=BIGINT}
ORDER BY a.update_date DESC

SELECT COUNT(1) FROM t_esp_goods_base_info a, ( SELECT a.goods_id goodsId, a.id goodsStockId, b.minprice minPrice FROM t_esp_goods_stock_info a, ( SELECT goods_id goodsId, MIN(market_price) minprice FROM t_esp_goods_stock_info WHERE stock_curr_amt > 0 GROUP BY goods_id ) b WHERE a.goods_id = b.goodsId AND a.market_price = b.minprice GROUP BY a.goods_id ) c WHERE a.id = c.goodsId AND CURRENT_TIMESTAMP > a.list_time AND CURRENT_TIMESTAMP < a.delist_time AND STATUS = 'G02' AND is_delete = '01'
AND a.category_id1 = #{categoryId1, jdbcType=BIGINT}
AND a.category_id2 = #{categoryId2, jdbcType=BIGINT}
AND a.category_id3 = #{categoryId3, jdbcType=BIGINT}

SELECT a.id goodId, b.id goodsStockId, a.goods_name goodsName, a.goods_title goodsTitle, a.goods_logo_url goodsLogoUrl, a.source source FROM t_esp_goods_base_info a LEFT JOIN ( SELECT id id, goods_id goods_id, SUM(sales_num) sales_num FROM t_esp_goods_sales_volume GROUP BY goods_id ) c ON a.id = c.goods_id LEFT JOIN ( SELECT m.goods_id goods_id, m.id id, n.minprice goods_price FROM t_esp_goods_stock_info m, ( SELECT goods_id goodsId, MIN(goods_price) minprice FROM t_esp_goods_stock_info GROUP BY goods_id ) n WHERE m.goods_id = n.goodsId AND m.goods_price = n.minprice GROUP BY m.goods_id ) b ON a.id = b.goods_id WHERE a.`status` = 'G02' AND a.is_delete = '01' AND CURRENT_TIMESTAMP > a.list_time AND CURRENT_TIMESTAMP < a.delist_time
AND a.category_id1 = #{categoryId1, jdbcType=BIGINT}
AND a.category_id2 = #{categoryId2, jdbcType=BIGINT}
AND a.category_id3 = #{categoryId3, jdbcType=BIGINT}
ORDER BY a.list_time DESC
ORDER BY c.sales_num DESC
ORDER BY a.newCreat_date DESC
ORDER BY a.newCreat_date ASC
ORDER BY b.goods_price DESC
ORDER BY b.goods_price ASC
limit #{page, jdbcType=INTEGER},#{rows, jdbcType=INTEGER}

SELECT count(1) FROM t_esp_goods_base_info a LEFT JOIN ( SELECT id id, goods_id goods_id, SUM(sales_num) sales_num FROM t_esp_goods_sales_volume GROUP BY goods_id ) c ON a.id = c.goods_id LEFT JOIN ( SELECT m.goods_id goods_id, m.id id, n.minprice goods_price FROM t_esp_goods_stock_info m, ( SELECT goods_id goodsId, MIN(goods_price) minprice FROM t_esp_goods_stock_info GROUP BY goods_id ) n WHERE m.goods_id = n.goodsId AND m.goods_price = n.minprice GROUP BY m.goods_id ) b ON a.id = b.goods_id WHERE a.`status` = 'G02' AND a.is_delete = '01' AND CURRENT_TIMESTAMP > a.list_time AND CURRENT_TIMESTAMP < a.delist_time
AND a.category_id1 = #{categoryId1, jdbcType=BIGINT}
AND a.category_id2 = #{categoryId2, jdbcType=BIGINT}
AND a.category_id3 = #{categoryId3, jdbcType=BIGINT}

SELECT DISTINCT (t.ID) goodId FROM t_esp_goods_base_info t LEFT JOIN t_esp_category c ON ( t.category_id1 = c.id OR t.category_id2 = c.id OR t.category_id3 = c.id ) LEFT JOIN ( SELECT id id, goods_id goods_id, SUM(sales_num) sales_num FROM t_esp_goods_sales_volume GROUP BY goods_id ) s ON t.id = s.goods_id WHERE ( t.goods_name LIKE '%${goodsName}%' OR c.category_name LIKE '%${goodsName}%' ) AND t.`status` = 'G02' AND t.is_delete = '01' AND CURRENT_TIMESTAMP > t.list_time AND CURRENT_TIMESTAMP < t.delist_time
ORDER BY s.sales_num DESC
limit #{page, jdbcType=INTEGER},#{rows, jdbcType=INTEGER}
