mapper.select:
SELECT id id, goods_id goodsId, goods_sku_attr goodsSkuAttr, goods_price goodsPrice, stock_total_amt stockTotalAmt, stock_curr_amt stockCurrAmt, create_user createUser, update_user updateUser, create_date createDate, update_date updateDate, market_price marketPrice, goods_Cost_Price goodsCostPrice, stock_Logo stockLogo, sku_id skuId, goods_compare_url goodsCompareUrl, goods_compare_url2 goodsCompareUrl2 FROM t_esp_goods_stock_info WHERE 1=1 and delete_flag = 'N'
AND id = #{id, jdbcType=BIGINT}
AND goods_id = #{goodsId, jdbcType=BIGINT}
ORDER BY update_date DESC


SELECT COUNT(1) FROM t_esp_goods_stock_info 
WHERE 1=1 and delete_flag = 'N'


AND id = #{id, jdbcType=BIGINT}


AND goods_id = #{goodsId, jdbcType=BIGINT}

mapper.update:
update t_esp_goods_stock_info A
A.update_date = CURRENT_TIMESTAMP, A.update_user = #{updateUser, jdbcType=VARCHAR}, A.goods_sku_attr = #{goodsSkuAttr}, A.stock_total_amt = #{stockTotalAmt, jdbcType=BIGINT}, A.stock_curr_amt = #{stockCurrAmt, jdbcType=BIGINT}, A.goods_price = #{goodsPrice, jdbcType=BIGINT}, A.market_price = #{marketPrice, jdbcType=BIGINT}, A.goods_Cost_Price = #{goodsCostPrice, jdbcType=BIGINT}, A.stock_logo = #{stockLogo, jdbcType=VARCHAR}, A.sku_id = #{skuId, jdbcType=VARCHAR}, A.attr_val_ids = #{attrValIds, jdbcType=VARCHAR}, A.delete_flag = #{deleteFlag, jdbcType=VARCHAR},
WHERE A.ID = #{id, jdbcType=BIGINT}

mapper.delete:
delete from t_esp_goods_stock_info where goods_id in (

    #{goodsId}

)
