mapper.select:
SELECT id id, order_id orderId, user_id userId, payType payType, order_amt orderAmt, paystatus payStatus, remark remark, orderstatus status, logistics_name logisticsName, logistics_no logisticsNo, province province, city city, district district, address address, postcode postcode, name name, telephone telephone, acceptgoodstype acceptGoodsType, extend_acceptgoodsNum extendAcceptGoodsNum, send_outgoodsdate sendOutGoodsDate, last_acceptgoodsdate lastAcceptGoodsDate, acceptgoodsdate acceptGoodsDate, addressId addressId, down_payment_amount downPaymentAmount, create_date createDate, update_date updateDate, pre_delivery preDelivery, coupon_id couponId FROM t_esp_order_info WHERE 1=1 and ext_parent_id!='-1'
AND user_id=#{userId}
AND orderstatus in
#{item}
ORDER BY update_date DESC

SELECT id id, order_id orderId, user_id userId, payType payType, order_amt orderAmt, paystatus payStatus, remark remark, orderstatus status, logistics_name logisticsName, logistics_no logisticsNo, logistics_sign_time logisticsSignDate, merchant_code merchantCode, province province, city city, district district, address address, postcode postcode, name name, telephone telephone, acceptgoodstype acceptGoodsType, extend_acceptgoodsNum extendAcceptGoodsNum, send_outgoodsdate sendOutGoodsDate, last_acceptgoodsdate lastAcceptGoodsDate, acceptgoodsdate acceptGoodsDate, addressId addressId, main_order_id mainOrderId, down_payment_amount downPaymentAmount, create_date createDate, update_date updateDate, pre_delivery preDelivery, source source, ext_order_id extOrderId, ext_parent_id extParentId, coupon_id couponId, parent_order_id parentOrderId FROM t_esp_order_info WHERE 1=1 and ext_parent_id!='-1'
AND user_id=#{userId}
AND order_id=#{orderId}
ORDER BY update_date DESC

mapper.update:
UPDATE t_esp_order_info
SET update_date = CURRENT_TIMESTAMP,
	orderstatus = #{status}
WHERE order_id =  #{orderId}

UPDATE t_esp_order_info
is_delete = '01', update_date = CURRENT_TIMESTAMP
WHERE order_id = #{orderId, jdbcType=VARCHAR}

UPDATE t_esp_order_info
parent_order_id = #{parentOrderId, jdbcType=VARCHAR}, orderstatus = #{status, jdbcType=VARCHAR}, pre_delivery = #{preDelivery, jdbcType=VARCHAR}, update_date = CURRENT_TIMESTAMP
WHERE order_id = #{orderId, jdbcType=VARCHAR}

mapper.select:
		SELECT count(1) from t_esp_order_info
		where  orderstatus =#{orderStatus} and ext_parent_id!='-1'
		and create_date >= #{dateBegin}
        and create_date < #{dateEnd}

SELECT sum(order_amt) from t_esp_order_info
where  orderstatus =#{orderStatus} and ext_parent_id!='-1'
and pay_time >= #{dateBegin}
and pay_time <= #{dateEnd}

SELECT id id, order_id orderId, user_id userId, payType payType, pay_time payTime, order_amt orderAmt, paystatus payStatus, orderstatus status, logistics_name logisticsName, logistics_no logisticsNo, acceptgoodstype acceptGoodsType, extend_acceptgoodsNum extendAcceptGoodsNum, send_outgoodsdate sendOutGoodsDate, last_acceptgoodsdate lastAcceptGoodsDate, acceptgoodsdate acceptGoodsDate from t_esp_order_info where ext_parent_id!='-1' AND orderstatus in
#{item}
and create_date >= #{dateBegin} and create_date < #{dateEnd}

SELECT order_id orderId, user_id userId, order_amt orderAmt, orderstatus status, create_date createDate, update_date updateDate FROM t_esp_order_info where 1=1
and create_date >= #{beginDate}
and create_date < #{endDate}

SELECT orderinfo.id orderPrimayId, orderinfo.order_id orderId, orderinfo.ext_order_id extOrderId, orderinfo.merchant_code merchantCode, refundinfo.order_id refundOrderId, orderinfo.telephone telephone, sum(orderinfordetail.discount_amount) totalDiscountAmount, orderinfo.create_date createDate, orderinfo.`name` name from t_esp_order_info orderinfo LEFT JOIN t_esp_refund_info refundinfo on orderinfo.order_id = refundinfo.order_id LEFT JOIN t_esp_order_detail_info orderinfordetail on orderinfo.order_id = orderinfordetail.order_id where orderinfo.orderstatus in
#{item}
and orderinfo.update_date >= #{dateBegin} and orderinfo.update_date < #{dateEnd} GROUP BY orderinfo.order_id ORDER BY orderinfo.order_id

SELECT orderInfo.order_id orderId, orderInfo.main_order_id mainOrderId, merchant.merchant_name merchantName, orderInfo.user_id userId, orderInfo.create_date createDate, orderInfo.pay_time payTime, orderInfo.orderstatus orderstatus, MAX(txnInfo.txn_type) txnType, orderInfo.order_amt orderAmt FROM t_esp_order_info orderInfo LEFT JOIN t_esp_merchant_info merchant ON orderInfo.merchant_code = merchant.merchant_code LEFT JOIN t_apass_txn_info txnInfo ON orderInfo.main_order_id = txnInfo.order_id WHERE orderInfo.orderstatus in ('D02','D03','D04','D05')
AND orderInfo.create_date >= #{beginDate}
AND orderInfo.create_date < CURRENT_TIMESTAMP GROUP BY orderInfo.order_id ORDER BY orderInfo.orderstatus, orderInfo.create_date, orderInfo.order_id DESC
