mapper.select:
SELECT id id, order_id orderId, user_id userId, payType payType, order_amt orderAmt, paystatus payStatus, remark remark, orderstatus status, logistics_name logisticsName, logistics_no logisticsNo, province province, city city, district district, address address, postcode postcode, name name, telephone telephone, acceptgoodstype acceptGoodsType, extend_acceptgoodsNum extendAcceptGoodsNum, send_outgoodsdate sendOutGoodsDate, last_acceptgoodsdate lastAcceptGoodsDate, acceptgoodsdate acceptGoodsDate, addressId addressId, down_payment_amount downPaymentAmount, create_date createDate, update_date updateDate, pre_delivery preDelivery FROM t_esp_order_info WHERE 1=1 and ext_parent_id!='-1'
AND user_id=#{userId}
AND orderstatus in
#{item}
ORDER BY update_date DESC

SELECT id id, order_id orderId, user_id userId, payType payType, order_amt orderAmt, paystatus payStatus, remark remark, orderstatus status, logistics_name logisticsName, logistics_no logisticsNo, logistics_sign_time logisticsSignDate, merchant_code merchantCode, province province, city city, district district, address address, postcode postcode, name name, telephone telephone, acceptgoodstype acceptGoodsType, extend_acceptgoodsNum extendAcceptGoodsNum, send_outgoodsdate sendOutGoodsDate, last_acceptgoodsdate lastAcceptGoodsDate, acceptgoodsdate acceptGoodsDate, addressId addressId, main_order_id mainOrderId, down_payment_amount downPaymentAmount, create_date createDate, update_date updateDate, pre_delivery preDelivery, source source, ext_order_id extOrderId, ext_parent_id extParentId FROM t_esp_order_info WHERE 1=1 and ext_parent_id!='-1'
AND user_id=#{userId}
AND order_id=#{orderId}
ORDER BY update_date DESC

mapper.update:
	
	UPDATE t_esp_order_info 
	
	
		update_date = CURRENT_TIMESTAMP,
		orderstatus = #{status, jdbcType=VARCHAR}
	

	WHERE order_id =  #{orderId, jdbcType=VARCHAR}

	
	UPDATE t_esp_order_info 
	
	
		is_delete = '01',
		update_date = #{updateDate,jdbcType=TIMESTAMP}
	

	WHERE order_id =  #{orderId, jdbcType=VARCHAR}

mapper.select:
		SELECT count(1) from t_esp_order_info
		where  orderstatus =#{orderStatus}
		and create_date >= #{dateBegin}
        and create_date < #{dateEnd}

SELECT sum(order_amt) from t_esp_order_info
where  orderstatus =#{orderStatus}
and pay_time >= #{dateBegin}
and pay_time <= #{dateEnd}

SELECT order_id orderId, user_id userId, order_amt orderAmt, orderstatus status, create_date createDate, update_date updateDate FROM t_esp_order_info where 1=1
and create_date >= #{beginDate}
and create_date < #{endDate}
