mapper.select:
select refundInfo.id refundId, refundInfo.r_logistics_name rLogisticsName, refundInfo.r_logistics_no rLogisticsNo, refundInfo.s_logistics_name sLogisticsName, refundInfo.s_logistics_name sLogisticsNameDes, refundInfo.s_logistics_no sLogisticsNo, refundInfo.order_id orderId, refundInfo.refund_type refundType, refundInfo.refund_reason refundReason, refundInfo.refund_reason refundReasonDes, refundInfo.status status, refundInfo.status statusDesc, refundInfo.is_agree isAgree, refundInfo.remark remark, refundInfo.goods_url goodsUrl, refundInfo.create_date createDate, refundInfo.approval_user approvalUser, refundInfo.approval_comments approvalComments, detailInfo.goods_num goodsNum, detailInfo.goods_price goodsPrice from t_esp_refund_info refundInfo left join t_esp_refund_detail_info detailInfo on detailInfo.order_id=refundInfo.order_id
refundInfo.order_id=#{orderId}
ORDER BY refundInfo.create_date DESC
